// Utility function to check if a user is authenticated
export const isUserAuthenticated = async () => {
  try {
    const { data, error } = await supabase.auth.getSession();
    return { 
      isAuthenticated: !!data.session, 
      session: data.session, 
      error 
    };
  } catch (error) {
    console.error("Error checking authentication:", error);
    return { 
      isAuthenticated: false, 
      session: null, 
      error 
    };
  }
};

// Utility function to get current user profile
export const getCurrentUserProfile = async () => {
  try {
    const { data: { session } } = await supabase.auth.getSession();
    if (!session) return { profile: null, error: new Error('No active session') };
    
    try {
      const { data, error } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', session.user.id)
        .single();
        
      if (error) throw error;
      return { profile: data, error: null };
    } catch (error) {
      console.error("Error fetching profile:", error);
      return { profile: null, error };
    }
  } catch (error) {
    console.error("Error getting session:", error);
    return { profile: null, error };
  }
};

// Improved test connection to Supabase with detailed error reporting
export const testSupabaseConnection = async () => {
  console.log("Testing Supabase connection...");
  
  try {
    // Try to ping the Supabase API
    const startTime = Date.now();
    const { error } = await supabase.from('profiles').select('count', { count: 'exact', head: true });
    const endTime = Date.now();
    const latency = endTime - startTime;
    
    if (error) {
      if (error.code === "PGRST116") {
        // This is a permission error, which actually indicates the connection is working
        // but the user doesn't have access to the profiles table
        console.log("✅ Supabase connection successful (permission error, but connection works)");
        return { 
          success: true, 
          latency,
          message: "Connected (no table access)",
          error: null 
        };
      }
      
      console.error("❌ Supabase connection failed:", error);
      return { 
        success: false, 
        latency,
        message: `Error: ${error.message || error.code || "Unknown error"}`,
        error 
      };
    }
    
    console.log(`✅ Supabase connection successful (latency: ${latency}ms)`);
    return { 
      success: true, 
      latency,
      message: `Connected (latency: ${latency}ms)`,
      error: null 
    };
  } catch (error) {
    console.error("❌ Supabase connection failed:", error);
    return { 
      success: false, 
      latency: 0,
      message: error instanceof Error ? error.message : "Unknown connection error",
      error 
    };
  }
};

